import * as React from 'react';
import { styled, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import MuiDrawer from '@mui/material/Drawer';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import CssBaseline from '@mui/material/CssBaseline';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import logo from '../img/logo/logo.gif'
import { Link } from "react-router-dom";
import HomeIcon from "@mui/icons-material/Home";
import Tooltip from '@mui/material/Tooltip';
import PersonIcon from "@mui/icons-material/Person";
import EmojiObjectsIcon from '@mui/icons-material/EmojiObjects';


const drawerWidth = 240;

const openedMixin = (theme) => ({
  width: drawerWidth,
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen,
  }),
  overflowX: 'hidden',
});

const closedMixin = (theme) => ({
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  overflowX: 'hidden',
  width: `calc(${theme.spacing(7)} + 1px)`,
  [theme.breakpoints.up('sm')]: {
    width: `calc(${theme.spacing(8)} + 1px)`,
  },
});

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
}));

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  zIndex: theme.zIndex.drawer + 1,
  transition: theme.transitions.create(['width', 'margin'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const Drawer = styled(MuiDrawer, { shouldForwardProp: (prop) => prop !== 'open' })(
  ({ theme, open }) => ({
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: 'nowrap',
    boxSizing: 'border-box',
    ...(open && {
      ...openedMixin(theme),
      '& .MuiDrawer-paper': openedMixin(theme),
    }),
    ...(!open && {
      ...closedMixin(theme),
      '& .MuiDrawer-paper': closedMixin(theme),
    }),
  }),
);

export default function Sidebar() {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  const handleListItemClick = (event, index) => {
    setSelectedIndex(index);
  };

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" open={open} style={{backgroundColor:"#2B2785"}}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{
              marginRight: 5,
              ...(open && { display: 'none' }),
            }}
          >
            <MenuIcon />
          </IconButton>
          <img style={{width:'45px',height:'40px',margin:'10px'}} src={logo} alt="img" />
          <Typography variant="subtitle1" sx={{color: '#ffffff',fontWeight: 'bold',fontSize: '24px'}}>
           ระบบเบิกจ่ายพัสดุ
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer variant="permanent" open={open} PaperProps={{
        sx: {
          height: '100vh',
          backgroundColor: "#6592FD"
        }
      }}>
        <DrawerHeader style={{backgroundColor:'#6592FD'}}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </DrawerHeader>
        <Divider sx={{backgroundColor:'#000'}} />
        <Link exact to="/PageHome" style={{ textDecoration: "none", color: "black" }} >
          <List style={{backgroundColor:'#6592FD'}}>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
            >
              <Tooltip title="Home">
              <HomeIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></HomeIcon>
              </Tooltip>
              <ListItemText
                primary="HOME"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider />
          <Divider sx={{backgroundColor:'#000'}} />
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="รายการพัสดุ">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="รายการพัสดุ"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="ดูรายการที่เบิก">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="ดูรายการที่เบิก"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="อนุมัติการขอเบิก">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="อนุมัติการขอเบิก"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="รายงาน">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="รายงาน"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="จัดการสมาชิก">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="จัดการสมาชิก"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
          <Link to="#" style={{ textDecoration: "none", color: "black" }}>
          <List>
            <ListItemButton
              sx={{
                height: 50,
                justifyContent: open ? "initial" : "center",
                px: 2.5,
              }}
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
            >
               <Tooltip title="Log out">
              <PersonIcon
                sx={{
                  minWidth: 0,
                  mr: open ? 3 : "auto",
                  justifyContent: "center",
                }}
              ></PersonIcon>
              </Tooltip>
              <ListItemText
                primary="Log out"
                sx={{ opacity: open ? 1 : 0 }}
              />
            </ListItemButton>
          </List>
          </Link>
          <Divider sx={{backgroundColor:'#000'}}/>
      </Drawer>
      
      <Box  sx={{ height:'100vh',background:'#5F59F7',color:'#FFFFFF' }}>
        <DrawerHeader />
        <Typography paragraph>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
          tempor incididunt ut labore et dolore magna aliqua. Rhoncus dolor purus non
          enim praesent elementum facilisis leo vel. Risus at ultrices mi tempus
          imperdiet. Semper risus in hendrerit gravida rutrum quisque non tellus.
          Convallis convallis tellus id interdum velit laoreet id donec ultrices.
          Odio morbi quis commodo odio aenean sed adipiscing. Amet nisl suscipit
          adipiscing bibendum est ultricies integer quis. Cursus euismod quis viverra
          nibh cras. Metus vulputate eu scelerisque felis imperdiet proin fermentum
          leo. Mauris commodo quis imperdiet massa tincidunt. Cras tincidunt lobortis
          feugiat vivamus at augue. At augue eget arcu dictum varius duis at
          consectetur lorem. Velit sed ullamcorper morbi tincidunt. Lorem donec massa
          sapien faucibus et molestie ac.
        </Typography>
        <Typography paragraph>
          Consequat mauris nunc congue nisi vitae suscipit. Fringilla est ullamcorper
          eget nulla facilisi etiam dignissim diam. Pulvinar elementum integer enim
          neque volutpat ac tincidunt. Ornare suspendisse sed nisi lacus sed viverra
          tellus. Purus sit amet volutpat consequat mauris. Elementum eu facilisis
          sed odio morbi. Euismod lacinia at quis risus sed vulputate odio. Morbi
          tincidunt ornare massa eget egestas purus viverra accumsan in. In hendrerit
          gravida rutrum quisque non tellus orci ac. Pellentesque nec nam aliquam sem
          et tortor. Habitant morbi tristique senectus et. Adipiscing elit duis
          tristique sollicitudin nibh sit. Ornare aenean euismod elementum nisi quis
          eleifend. Commodo viverra maecenas accumsan lacus vel facilisis. Nulla
          posuere sollicitudin aliquam ultrices sagittis orci a.
        </Typography>
      </Box>
    </Box>
  );
}
