import React,{useState} from 'react'
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import { styled } from '@mui/material/styles';
import {
  DataGrid,
  GridToolbarQuickFilter,
  GridLinkOperator,
} from '@mui/x-data-grid';
import Container from '@mui/material/Container';
import Button from "@mui/material/Button";
import AddIcon from '@mui/icons-material/Add';



const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
}));


function QuickSearchToolbar() {
  return (
    <Box
      sx={{
        p: 0.5,
        pb: 0,
      }}
    >
      <GridToolbarQuickFilter
        quickFilterParser={(searchInput) =>
          searchInput
            .split(',')
            .map((value) => value.trim())
            .filter((value) => value !== '')
        }
      />
    </Box>
  );
}

const ButtonDecrease = styled(Button)(({ theme }) => ({
  color: theme.palette.getContrastText('#ffffff'),
  backgroundColor: "#8C61FF",
  borderColor:'#8C61FF',
  fontWeight: 'bold',
}));
const ButtonIncrease = styled(Button)(({ theme }) => ({
  color: theme.palette.getContrastText('#ffffff'),
  backgroundColor: "#2196f3",
  borderColor:'#2196f3',
  fontWeight: 'bold',
}));

const ButtonAdd = styled(Button)(({ theme }) => ({
  color: theme.palette.getContrastText('#ffffff'),
  backgroundColor: "#ffffff",
  borderColor:'#000000',
  fontWeight: 'bold',
  '&:hover': {
    backgroundColor: "#8C61FF",
    borderColor:'#8C61FF',
    color:'#ffffff'
  },
}));


function PageViewItem() {
  return (
    <div>
           <Box sx={{width: "100%",height: "100%"}}>
        <DrawerHeader />

            <Box sx={{textAlign: "center"}}>
            <Typography
              variant="subtitle1"
              sx={{ color: "#ffffff", fontWeight: "bold", fontSize: "28px" }}
            >
              รายการพัสดุ
            </Typography>
            </Box>
            <DrawerHeader />
            <Container >
            <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                startIcon={<AddIcon />}
               
              >
                เพิ่มรายการพัสดุ
              </ButtonAdd>

            <Box sx={{ height: 400, width: 1 ,backgroundColor:'#ffffff'}}>
      <DataGrid
         rows={rows}
        columns={columns}
        initialState={{
          filter: {
            filterModel: {
              items: [],
              quickFilterLogicOperator: GridLinkOperator.Or,
            },
          },
        }}
        components={{ Toolbar: QuickSearchToolbar }}
      />
    </Box>
    </Container>
        </Box>
    </div>
  )
}

export default PageViewItem
