import React,{useState} from 'react'
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import { styled } from '@mui/material/styles';
import {
  DataGrid,
  GridToolbarQuickFilter,
  GridLinkOperator,
} from '@mui/x-data-grid';
import Container from '@mui/material/Container';
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import IncreaseAndDecrease from '../components/IncreaseAndDecrease'
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import RemoveIcon from '@mui/icons-material/Remove';
import ButtonEdit from '../components/EditParcel'











const ButtonAdd = styled(Button)(({ theme }) => ({
  color: theme.palette.getContrastText('#ffffff'),
  backgroundColor: "#ffffff",
  borderColor:'#000000',
  fontWeight: 'bold',
  '&:hover': {
    backgroundColor: "#8C61FF",
    borderColor:'#8C61FF',
    color:'#ffffff'
  },
}));


const ButtonDelete = styled(Button)(({ theme }) => ({
  color: theme.palette.getContrastText('#ffffff'),
  backgroundColor: "#ffffff",
  borderColor:'#f44336',
  fontWeight: 'bold',
  '&:hover': {
    backgroundColor: "#f44336",
    borderColor:'#f44336',
    color:"#ffffff"
  },
}));







const columns = [
    { field: 'id', headerName: 'ลำดับ', width: 90 },
    {
      field: 'parcelnumber',
      headerName: 'เลขพัสดุ',
      width: 150,
      editable: true,
    },
    {
      field: 'parcelname',
      headerName: 'ชื่อพัสดุ',
      width: 150,
      editable: true,
    },
    {
      field: 'quantity',
      headerName: 'จำนวน',
      type: 'number',
      width: 110,
      editable: true,
    },
    {
        field: 'unit',
        headerName: 'หน่วย',
        width: 50,
        editable: true,
      },
      {
        field: 'status',
        headerName: 'สถานะ',
        width: 150,
        renderCell: (params) => (
          <strong>
          <p style={{color: '#4caf50'}}>ว่าง</p>
          </strong>
        ),
      },
      {
        field: 'withdraw',
        headerName: 'เบิก',
        width: 150,
        renderCell: (params) => {
          return (
            <>
          <strong>
            <IncreaseAndDecrease />
          </strong>
            </>
          )
          
      },
      },
      {
        field: 'management',
        headerName: 'การจัดการ',
        width: 250,
        renderCell: (params) => (
          <strong>
            <Box sx={{display: 'flex'}}>
            <ButtonEdit />

<Link to="#" style={{ textDecoration: "none" }}>
  <ButtonDelete
    variant="outlined"
    color="primary"
    size="small"
