    color: theme.palette.getContrastText('#ffffff'),
    backgroundColor: "#8C61FF",
    borderColor:'#8C61FF',
    fontWeight: 'bold',
  }));
  const ButtonIncrease = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText('#ffffff'),
    backgroundColor: "#2196f3",
    borderColor:'#2196f3',
    fontWeight: 'bold',
  }));

function PageParcelList() {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [counter, setCounter] = useState(1);
  const incrementCounter = () => setCounter(counter + 1);
  let decrementCounter = () => setCounter(counter - 1);
  if(counter<=0) {
    decrementCounter = () => setCounter(1);
  }
  
  return (
    <div>
        <Box sx={{width: "100%",height: "100%"}}>
        <DrawerHeader />

            <Box sx={{textAlign: "center"}}>
            <Typography
              variant="subtitle1"
              sx={{ color: "#ffffff", fontWeight: "bold", fontSize: "28px" }}
            >
              รายการพัสดุ
            </Typography>
            </Box>
            
            <Container >
            <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                startIcon={<AddIcon />}
                onClick={handleClickOpen}
              >
                เพิ่มรายการพัสดุ
              </ButtonAdd>
              <Dialog
              fullWidth='true'
              maxWidth={'sm'}
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title" sx={{textAlign: "center"}}>
          <p style={{fontSize: '24px',fontWeight: 'bold'}}>เพิ่มรายการพัสดุ</p>
        </DialogTitle>
        <DialogContent>
        <Box sx={{display: "flex",justifyContent: 'center',alignItems: 'center',flexDirection:'column'}}>
          <Box sx={{margin:'10px',width: "100%"}}>
            <TextField id="outlined-basic" label="เลขพัสดุ" variant="outlined" fullWidth/>
          </Box>
          <Box sx={{margin:'10px',width: "100%"}}>
            <TextField id="outlined-basic" label="ชื่อพัสดุ" variant="outlined" fullWidth/>
          </Box>
          <Box sx={{margin:'10px',width: "100%",display:'flex'}}>
            <Box sx={{width: "80%"}}>
            <TextField id="outlined-basic" label="จำนวน"  InputLabelProps={{
            shrink: true,
          }} value={counter} variant="outlined" fullWidth/>
            </Box>
            <Box sx={{display: "flex",width: "20%"}}>
            <Box sx={{margin:'10px'}}>
              <ButtonIncrease onClick={incrementCounter} style={{maxWidth: '40px', maxHeight: '40px', minWidth: '40px', minHeight: '40px'}}  variant="contained" ><AddIcon /> </ButtonIncrease>   
              </Box>
              <Box sx={{margin:'10px'}}>
              <ButtonDecrease onClick={decrementCounter} style={{maxWidth: '40px', maxHeight: '40px', minWidth: '40px', minHeight: '40px'}}  variant="contained" ><RemoveIcon /> </ButtonDecrease>   
              </Box>
            </Box>
          </Box>
          <Box sx={{margin:'10px',width: "100%"}}>
            <TextField id="outlined-basic" label="หน่วย" variant="outlined" fullWidth/>
          </Box>
        </Box>
        </DialogContent>
        <DialogActions>
        <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                
              >
                บันทึก
              </ButtonAdd>
              <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                onClick={handleClose}
              >
                ยกเลิก
              </ButtonAdd>
        </DialogActions>
      </Dialog>

            <Box sx={{ height: 400, width: 1 ,backgroundColor:'#ffffff'}}>
      <DataGrid
         rows={rows}
        columns={columns}
        initialState={{
          filter: {
            filterModel: {
              items: [],
              quickFilterLogicOperator: GridLinkOperator.Or,
            },
          },
        }}
        components={{ Toolbar: QuickSearchToolbar }}
      />
    </Box>
    <Box sx={{display: 'flex',justifyContent: 'center',alignItems: 'center'}}>
    <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                sx={{margin:'20px'}}
              >
                ยืนยันการเบิก
              </ButtonAdd>
              <ButtonAdd
                variant="outlined"
                color="primary"
                size="small"
                sx={{margin:'20px'}}
              >
                ยกเลิก
              </ButtonAdd>
    </Box>
    </Container>
        </Box>
    </div>
  )
}

